document.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelectorAll("#portfolio ul.projects > li"),l=e.length,t=document.querySelector("#portfolio ul.carousel li i.fa-chevron-circle-right"),c=document.querySelector("#portfolio ul.carousel li i.fa-chevron-circle-left"),o=document.querySelector("#portfolio ul.carousel ul.dots");let i=0,r=()=>{o.innerHTML="";for(let e=0;e<l;e++)o.innerHTML+=e==i?'<li class="active"></li>':"<li></li>"};r();t.addEventListener("click",(()=>{e[i].classList.remove("active"),i<l-1?i++:i=0,e[i].classList.add("active"),r()})),c.addEventListener("click",(()=>{e[i].classList.remove("active"),i>0?i--:i=l-1,e[i].classList.add("active"),r()}))}));