document.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelector("header .fa-bars"),t=document.querySelector("header nav"),s=document.querySelectorAll("header nav ul li");e.addEventListener("click",(()=>{t.classList.toggle("discover"),e.classList.toggle("fa-times")}));for(let l=0;l<s.length;l++)s[l].addEventListener("click",(()=>{t.classList.toggle("discover"),e.classList.toggle("fa-times")}))}));