document.addEventListener("DOMContentLoaded",(()=>{let o=document.querySelector("#portfolio>ul.projects");fetch("../portfolio.json").then((t=>{t.ok&&t.json().then((t=>{for(let i=0;i<t.portfolio.length;i++){o.innerHTML+=`\n                                            <li>\n                                            <figure id="${t.portfolio[i].id}" itemscope itemtype="https://schema.org/Thing"\n                                            itemref="rockPaperScissors portfolioAymeline etchASketch">\n                                            <img itemprop="image" src="./assets/img/portfolio/${t.portfolio[i].img}" alt="${t.portfolio[i].title}">\n                                            <figcaption itemprop="description">\n                                            <p itemprop="name">${t.portfolio[i].title}</p>\n                                            <ul>\n                                            </ul>\n                                            <a itemprop="url" href="${t.portfolio[i].url}" target="_blank"\n                                            rel="noopener"><i class="far fa-eye"></i>Lien vers ${t.portfolio[i].title}\n                                            </a>\n                                            </figcaption>\n                                            </figure>\n                                            </li>`;for(let o=0;o<=t.portfolio[i].tools.length;o++)"database"==t.portfolio[i].tools[o]?document.querySelector(`#portfolio>ul>li:nth-child(${i+1}) figcaption ul`).innerHTML+=`<li> <i itemprop="instrument" class="fas fa-${t.portfolio[i].tools[o]}" aria-hidden="true"></li>`:document.querySelector(`#portfolio>ul>li:nth-child(${i+1}) figcaption ul`).innerHTML+=`<li> <i itemprop="instrument" class="devicon-${t.portfolio[i].tools[o]}-plain" aria-hidden="true"></i></li>`}document.querySelectorAll("#portfolio>ul>li")[0].classList.add("active")}))}))}));